INSERT INTO `profile` VALUES ('25', '研究院概况', '<p><span style=\"color:#040404;text-indent:32px;font-size:12pt;line-height:24px;font-family:楷体_GB2312;\">&nbsp; &nbsp; 研究院现有固定人员共有<span lang=\"EN-US\">11</span>人，包括教授<span lang=\"EN-US\">5</span>人，副教授<span lang=\"EN-US\">5</span>人；有留学或博士后科研经历的<span lang=\"EN-US\">8</span>人；有国家杰青<span lang=\"EN-US\">1</span>人，<span lang=\"EN-US\">973</span>首席科学家<span lang=\"EN-US\">1</span>人，山东省杰青<span lang=\"EN-US\">1</span>人，教育部新世纪优秀人才<span lang=\"EN-US\">1</span>人，山东省重点学科首席专家<span lang=\"EN-US\">1</span>人，学校<span lang=\"EN-US\">1361</span>人才工程<span lang=\"EN-US\">2</span>层次<span lang=\"EN-US\">1</span>人，<span lang=\"EN-US\">3</span>层次<span lang=\"EN-US\">2</span>人，引进人才<span lang=\"EN-US\">4</span>层次以上的<span lang=\"EN-US\">7</span>人。</span></p>\r\n<p><span style=\"color:#040404;text-indent:32px;font-size:12pt;line-height:24px;font-family:楷体_GB2312;\">&nbsp; &nbsp; 近年来，</span><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">共承担国家</span><span lang=\"EN-US\" style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">973</span><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">项目课题</span><span lang=\"EN-US\" style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">4</span><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">项，国家自然科学基金</span><span lang=\"EN-US\" style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">12</span><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">项，国家转基因重大专项课题</span><span lang=\"EN-US\" style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">2</span><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">项，教育部及省级课题</span><span lang=\"EN-US\" style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">20</span><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">余项。</span></p>\r\n<p><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">&nbsp; &nbsp; 现建设实验室</span><span lang=\"EN-US\" style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">500</span><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">多平方米，其中万级细胞培养室</span><span lang=\"EN-US\" style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">100</span><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">平米，拥有荧光显微镜及显微操作系统、荧光定量</span><span lang=\"EN-US\" style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">PCR</span><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">仪、化学发光检测仪、石蜡和冰冻切片机、电穿孔仪、冷冻离心机和多功能离心机等设备</span><span lang=\"EN-US\" style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">100</span><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">余件套，价值</span><span lang=\"EN-US\" style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">400</span><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">余万元。</span></p>\r\n<p><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">&nbsp; &nbsp; 培养研究生毕业</span><span lang=\"EN-US\" style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">30</span><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">余人，现在读研究生</span><span lang=\"EN-US\" style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">21</span><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">人，其中与外校联合培养博士生</span><span lang=\"EN-US\" style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">4</span><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">人。毕业研究生中考取博士</span><span lang=\"EN-US\" style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">13</span><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">人，获得国家和省级奖励者</span><span lang=\"EN-US\" style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">10</span><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">余人，获得学校奖励者</span><span lang=\"EN-US\" style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">30</span><span style=\"color:#040404;text-indent:24pt;font-family:楷体_GB2312;font-size:12pt;line-height:24px;\">余人。</span></p>');
INSERT INTO `profile` VALUES ('27', '学术委员会', '<p><span style=\"font-size:18px;font-family:Arial;\">主任委员：孙青原 研究员（中国科学院动物研究所）</span></p>\r\n<span style=\"font-family:Arial;\"> </span><p><span style=\"font-size:18px;font-family:Arial;\">委 &nbsp; &nbsp; &nbsp;员：</span><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">Massimo De Felici &nbsp;教授 （</span><span style=\"color:#040404;font-family:Arial;font-size:18px;line-height:19.5px;background-color:#f8fcff;\">University&nbsp;of&nbsp;Rome&nbsp;Tor&nbsp;Vergata</span><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">）</span></p>\r\n<span style=\"font-family:Arial;\"> </span><p><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">Martin Nyachoti &nbsp;教授（</span><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">University of Manitoba</span><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">）</span></p>\r\n<span style=\"font-family:Arial;\"> </span><p><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">Julang Li &nbsp;教授（</span><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">University of Guelph</span><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">）</span></p>\r\n<span style=\"font-family:Arial;\"> </span><p><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;</span><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">Gyu\r\nJin Rho 教授 （</span><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">Gyeongsang\r\nNational University</span><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">）</span></p>\r\n<p><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;Paul Dyce 教授 （</span><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">Auburn University</span><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">）</span></p>\r\n<p><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;史庆华 教授 （中国科技大学）</span></p>\r\n<p><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;沈 &nbsp; &nbsp;伟 教授 （青岛农业大学）</span></p>\r\n<p><span style=\"font-family:Arial;font-size:18px;line-height:1.5;\">&nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp; &nbsp;潘庆杰 教授 （青岛农业大学）</span></p>');
INSERT INTO `profile` VALUES ('28', '研究领域', '<p class=\"MsoNormal\"><span style=\"font-family:仿宋;font-size:18px;\">&nbsp; &nbsp; 干细胞与配子发生研究团队主要研究生殖干细胞和原始生殖细胞形成、增殖和分化，卵母细胞发生与卵泡发育，精子发生与成熟等生殖发育过程的内分泌调节及分子细胞机制。</span><span lang=\"EN-US\"><o:p></o:p></span></p>\r\n<span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\"> </span><p class=\"MsoNormal\"><span style=\"font-family:仿宋;font-size:18px;\">&nbsp; &nbsp; 受精与胚胎工程研究团队主要研究精子的运动和选择、卵子成熟、精卵结合、早期胚胎发育机制，及配子</span><span lang=\"EN-US\" style=\"font-family:\'Times New Roman\', serif;font-size:18px;\">/</span><span style=\"font-family:仿宋;font-size:18px;\">胚胎冷冻和性别控制等技术。</span><span lang=\"EN-US\"><o:p></o:p></span></p>\r\n<span style=\"font-size:18px;\"> </span><span style=\"font-size:18px;\"> </span><p class=\"MsoNormal\"><span style=\"font-family:仿宋;font-size:18px;\">&nbsp; &nbsp; 环境与生殖健康研究团队主要研究环境、营养及疾病对生殖力的遗传学、表观遗传学和生理学的调节作用，为人类生殖健康和动物健康养殖提供理论依据。</span><span lang=\"EN-US\"><o:p></o:p></span></p>');
